import { useEffect, useState } from "react";
import { useInput } from "../hooks/useInput";

const SimpleInput = (props) => {
  const {
    value: enteredName,
    hasError: nameInputHasError,
    valueChangeHandler: nameChangedHandler,
    inputBlurHandler: nameBlurHandler,
  } = useInput();
  const [name, setName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const enteredNameIsValid = name.trim() !== "";
  const nameInputIsIvalid = !enteredNameIsValid && enteredNameTouched;

  const [email, setEmail] = useState("");
  const [enteredEmailTouched, setEntereEmailTouched] = useState(false);

  const enteredEmailIsValid = email.includes("@");
  const emailInputIsInValid = !enteredEmailIsValid && enteredEmailTouched;

  let formsIsValid = false;

  if (enteredNameIsValid && enteredEmailIsValid) {
    formsIsValid = true;
  }

  const nameChangeHandler = (e) => {
    setName(e.target.value);
  };

  const nameInputBlurHandler = (e) => {
    setEnteredNameTouched(true);
  };
  const emailChangeHandler = (e) => {
    setEmail(e.target.value);
  };

  const emailInputBlurHandler = (e) => {
    setEntereEmailTouched(true);
  };

  const formSubmitHandler = (e) => {
    e.preventDefault();
    setEnteredNameTouched(true);

    setName(e.target.value);
    if (!enteredNameIsValid) {
      return;
    }
    //const inputValue = nameInputRef.current.value;
    setName("");
    setEnteredNameTouched(false);

    setEmail("");
    setEntereEmailTouched(false);
  };

  const nameInputClasses = nameInputIsIvalid
    ? "form-control invalid"
    : "form-control ";

  const emailInputClasses = emailInputIsInValid
    ? "form-control invalid"
    : "form-control ";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>

        <input
          type="text"
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameInputBlurHandler}
          value={name}
        />
        {nameInputIsIvalid && (
          <p className="error-text">Name must not be Empty</p>
        )}
      </div>

      <div className={emailInputClasses}>
        <label htmlFor="email">Your Email</label>

        <input
          type="email"
          id="email"
          onChange={emailChangeHandler}
          onBlur={emailInputBlurHandler}
          value={email}
        />
        {emailInputIsInValid && (
          <p className="error-text">Email must include @</p>
        )}
      </div>
      <div className="form-actions">
        <button
          disabled={!formsIsValid}
          className={!formsIsValid ? "btn-disabled" : ""}
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SimpleInput;
