import { useState } from "react";
import useInput2 from "../hooks/useInput2";
const BasicForm = (props) => {
  /*   const [firstName, setFirstName] = useState("");
  const [firstNameTouched, setFirstNameTouched] = useState(false);

  const firstNameIsValid = firstName.trim() !== "";
  const firstNameError = !firstNameIsValid && firstNameTouched; */
  const {
    enteredValue,
    isTouched,
    valueChangeHandler,
    touchHandler,
    hasError,
  } = useInput2((value) => value.trim() !== "");

  const firstNameClasses = hasError ? " form-control invalid" : "form-control";

  const firstNameSubmitHandler = (e) => {
    setFirstName(e.target.value);
  };
  const firstNameTouchHandler = (e) => {
    setFirstNameTouched(true);
  };

  const submitFormHandler = (e) => {
    e.preventDefault();

    if (!firstNameIsValid) return;
    console.log(firstName);
  };

  return (
    <form onSubmit={submitFormHandler}>
      <div className="control-group">
        <div className={firstNameClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onBlur={firstNameTouchHandler}
            onChange={firstNameSubmitHandler}
          />
          {firstNameError && (
            <p className="error-text">field cannot be empty</p>
          )}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
